import { useContext } from "react"
import CalendarContext, { View } from "../../Context/calendarContext"


const DateView = ({ className }: { className?: string }) => {
    const { date, setDate, setView } = useContext(CalendarContext)
    const dayElement = (_date: Date) => {
        return (
            <div key={_date.toISOString()}>{_date.getDate()}</div>
        )
    }
    return (
        <div className={className}>
            <div className='flex justify-around'>
                <div>{'<<'}</div>
                <div>{'<'}</div>
                <div>{date.monthName}</div>
                <div>{'>'}</div>
                <div>{'>>'}</div>
            </div>
            <div className='flex justify-around'>
                <div>
                    <div>Mon</div>
                    {date.daysColumns.Monday.map((date) => dayElement(date))}
                </div>
                <div>
                    <div>Tue</div>
                    {date.daysColumns.Tuesday.map((date) => dayElement(date))}
                </div>
                <div>
                    <div>Wed</div>
                    {date.daysColumns.Wednesday.map((date) => { return (<div key={date.toISOString()}>{date.getDate()}</div>) })}
                </div>
                <div>
                    <div>Thu</div>
                    {date.daysColumns.Thursday.map((date) => { return (<div key={date.toISOString()}>{date.getDate()}</div>) })}
                </div>
                <div>
                    <div>Fri</div>
                    {date.daysColumns.Friday.map((date) => { return (<div key={date.toISOString()}>{date.getDate()}</div>) })}
                </div>
                <div>
                    <div>Sat</div>
                    {date.daysColumns.Saturday.map((date) => { return (<div key={date.toISOString()}>{date.getDate()}</div>) })}
                </div>
                <div>
                    <div>Sun</div>
                    {date.daysColumns.Sunday.map((date) => { return (<div key={date.toISOString()}>{date.getDate()}</div>) })}
                </div>
            </div>
        </div>
    )
}

export default DateView
