enum days {
    Sunday,
    Monday,
    Tuesday,
    Wednesday,
    Thursday,
    Friday,
    Saturday
}


class Calendar {
    date: Date
    daysColumns: {
        Sunday: Array<Date>,
        Monday: Array<Date>,
        Tuesday: Array<Date>,
        Wednesday: Array<Date>,
        Thursday: Array<Date>,
        Friday: Array<Date>,
        Saturday: Array<Date>
    }
    private getDateColumns(_day: days) {
        let tempArray: Array<Date> = []
        const currentMonthFirstDay = new Date(this.date.getFullYear(), this.date.getMonth(), 1)
        const currentMonthLastDay = new Date(this.date.getFullYear(), this.date.getMonth() + 1, 0)
        const columnsStartDate = currentMonthFirstDay.getDay() === days.Sunday
            ? new Date(this.date.getFullYear(), this.date.getMonth(), -5)
            : new Date(this.date.getFullYear(), this.date.getMonth(), (days.Monday - currentMonthFirstDay.getDay()) + 1)
        console.log(columnsStartDate)
        return tempArray
    }
    constructor(_date: Date = new Date()) {
        this.date = _date
        this.daysColumns = {
            Sunday: this.getDateColumns(days.Sunday),
            Monday: this.getDateColumns(days.Monday),
            Tuesday: this.getDateColumns(days.Tuesday),
            Wednesday: this.getDateColumns(days.Wednesday),
            Thursday: this.getDateColumns(days.Thursday),
            Friday: this.getDateColumns(days.Friday),
            Saturday: this.getDateColumns(days.Saturday)
        }
    }
}



export default Calendar
