import { useContext } from "react"
import { MonthArray } from "../../Classes/calendarClass"
import CalendarContext from "../../Context/calendarContext"


const MonthView = ({ className }: { className: string }) => {
    const { date } = useContext(CalendarContext)
    const monthElement = (_month: string, _index: number) => {
        return (
            <div className={`${_month === date.monthName && 'bg-green-600'}`} key={_index}>{_month.slice(0, 3)}</div>
        )
    }
    return (
        <div className={className}>
            <div>{date.date.getFullYear()}</div>
            <div className='grid grid-cols-4'>
                {MonthArray.map((month, index) => monthElement(month, index))}
            </div>

        </div>
    )
}

export default MonthView
