import React, { useContext } from 'react'
import CalendarContext from '../../Context/calendarContext'

const YearView = ({ className }: { className?: string }) => {
    const { date } = useContext(CalendarContext)
    const yearRange = {
        start: date.date.getFullYear() - 17,
        stop: date.date.getFullYear() + 2
    }
    const yearArray = () => {
        let tempArray: Array<number> = []

        for (let i = yearRange.start; i < yearRange.stop; i++) {
            tempArray.push(i)
        }
        return tempArray
    }
    return (
        <div className={className}>
            <div>{`${yearRange.start}-${yearRange.stop}`}</div>
            <div>
                {yearArray().map((year, index) => {
                    return (
                        <div key={index}>{year}</div>
                    )
                }
                )}
            </div>
        </div>
    )
}

export default YearView
