import { useContext, useState } from "react"
import Calendar from "../../Classes/calendarClass"
import CalendarContext, { View } from "../../Context/calendarContext"


const DateView = ({ className }: { className?: string }) => {
    const { date, setDate, setView } = useContext(CalendarContext)
    const [dateView, setDateView] = useState(date.date)
    const dayElement = (_date: Date) => {
        return (
            <div key={_date.toISOString()}
                onClick={() => setDate(new Calendar(_date))}
                className={`
                ${_date.getDate() === date.date.getDate() && _date.getMonth() === date.date.getMonth() && _date.getFullYear() === date.date.getFullYear() && 'bg-green-500'
                    }
    `}>
                {_date.getDate()}
            </div>
        )
    }
    return (
        <div className={className}>
            <div className='flex justify-around'>
                <div>{'<<'}</div>
                <div>{'<'}</div>
                <div onClick={() => setView(View.Month)}>{date.monthName}</div>
                <div>{'>'}</div>
                <div>{'>>'}</div>
            </div>
            <div className='flex justify-around'>
                <div>
                    <div>Mon</div>
                    {date.daysColumns.Monday.map((date) => dayElement(date))}
                </div>
                <div>
                    <div>Tue</div>
                    {date.daysColumns.Tuesday.map((date) => dayElement(date))}
                </div>
                <div>
                    <div>Wed</div>
                    {date.daysColumns.Wednesday.map((date) => dayElement(date))}
                </div>
                <div>
                    <div>Thu</div>
                    {date.daysColumns.Thursday.map((date) => dayElement(date))}
                </div>
                <div>
                    <div>Fri</div>
                    {date.daysColumns.Friday.map((date) => dayElement(date))}
                </div>
                <div>
                    <div>Sat</div>
                    {date.daysColumns.Saturday.map((date) => dayElement(date))}
                </div>
                <div>
                    <div>Sun</div>
                    {date.daysColumns.Sunday.map((date) => dayElement(date))}
                </div>
            </div>
        </div>
    )
}

export default DateView
